import {
  FETCH_CONTENT,
  FETCH_CONTENT_SUCCESS,
  FETCH_CONTENT_FAILED,
} from "../action/type";

const initialState = {
  list: [],
  loading: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_CONTENT: {
      return {
        ...state,
        loading: true,
      };
    }
    case FETCH_CONTENT_SUCCESS: {
      return {
        ...state,
        list: [...action.payload],
        loading: false,
      };
    }
    case FETCH_CONTENT_FAILED: {
      return {
        ...state,
        loading: false,
      };
    }
    default:
      return state;
  }
}
